import React, { useState, useEffect } from 'react';
import { ShoppingCart, Heart } from 'lucide-react';
import { useCart } from '../contexts/CartContext';
import { useWishlist } from '../contexts/WishlistContext';
import CartDrawer from './CartDrawer';
import WishlistDrawer from './WishlistDrawer';

interface HeaderProps {
  className?: string;
}

const Header: React.FC<HeaderProps> = ({ className = '' }) => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isCartOpen, setIsCartOpen] = useState(false);
  const [isWishlistOpen, setIsWishlistOpen] = useState(false);

  const { cart } = useCart();
  const { wishlist } = useWishlist();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 100);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const toggleSubmenu = (e: React.MouseEvent<HTMLAnchorElement>) => {
    e.preventDefault();
    const submenu = e.currentTarget.nextElementSibling as HTMLElement;
    if (submenu) {
      submenu.classList.toggle('active');
    }
  };

  return (
    <>
      {/* Top Bar */}
      <div className="top-bar py-1">
        <div className="container">
          <div className="top-bar-content">
            <div className="top-bar-left"></div>
            <div className="top-bar-right text-xs">
              <a href="mailto:info@outpostcustom.co.uk">info@outpostcustom.co.uk</a>
              <span>Call 01562 227 117</span>
            </div>
          </div>
        </div>
      </div>

      {/* Main Header */}
      <header className={`main-header py-2 ${isScrolled ? 'scrolled' : ''} ${className}`}>
        <div className="container">
          <div className="header-content">
            {/* Logo */}
            <div className="logo">
              <a href="/">
                <img src="/images/outpost-logo.png" alt="Outpost Custom" />
              </a>
            </div>

            {/* Main Navigation */}
            <nav className="main-nav">
              <ul className="nav-menu">
                <li className="nav-item">
                  <a href="/" className="nav-link">HOME</a>
                </li>

                <li className="nav-item has-dropdown">
                  <a href="/shop" className="nav-link">SHOP </a>
                  <ul className="dropdown-menu">
                    <li><a href="/products">All Products</a></li>
                    <li><a href="/collections">Collections</a></li>
                    <li><a href="/collections/T-Shirts">T-Shirts</a></li>
                    <li><a href="/collections/Hoodies">Hoodies</a></li>
                    <li><a href="/collections/Caps">Caps & Hats</a></li>
                    <li><a href="/collections/Bags">Bags</a></li>
                  </ul>
                </li>

                <li className="nav-item has-dropdown">
                  <a href="#" className="nav-link">SERVICES </a>
                  <ul className="dropdown-menu">
                    <li><a href="#">Custom Printing</a></li>
                    <li><a href="#">Embroidery</a></li>
                    <li><a href="#">Signage</a></li>
                    <li><a href="#">Vehicle Graphics</a></li>
                  </ul>
                </li>

                <li className="nav-item">
                  <a href="#" className="nav-link">ABOUT</a>
                </li>
              </ul>
            </nav>

            {/* Contact Dropdown */}
            <div className="contact-nav">
              <div className="nav-item has-dropdown">
                <a href="#" className="nav-link">CONTACT US </a>
                <ul className="dropdown-menu dropdown-right">
                  <li><a href="#">Contact us</a></li>
                  <li><a href="#">Book a FREE consultation</a></li>
                  <li><a href="#">About Outpost Custom</a></li>
                  <li><a href="#">What we do</a></li>
                  <li><a href="#">Facebook</a></li>
                  <li><a href="#">Instagram</a></li>
                </ul>
              </div>
            </div>

            {/* Cart and Wishlist Actions */}
            <div className="header-actions" style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginLeft: '1rem' }}>
              {/* Wishlist Button */}
              <button
                onClick={() => setIsWishlistOpen(true)}
                className="action-button"
                style={{
                  position: 'relative',
                  background: 'transparent',
                  border: 'none',
                  cursor: 'pointer',
                  padding: '0.5rem',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  transition: 'all 0.3s ease'
                }}
                aria-label="Open wishlist"
              >
                <Heart
                  className="w-6 h-6"
                  style={{
                    color: wishlist.length > 0 ? '#6da71d' : '#666',
                    fill: wishlist.length > 0 ? '#6da71d' : 'none'
                  }}
                />
                {wishlist.length > 0 && (
                  <span
                    className="badge"
                    style={{
                      position: 'absolute',
                      top: '0',
                      right: '0',
                      background: '#6da71d',
                      color: 'white',
                      borderRadius: '50%',
                      width: '18px',
                      height: '18px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      fontSize: '11px',
                      fontWeight: 'bold'
                    }}
                  >
                    {wishlist.length}
                  </span>
                )}
              </button>

              {/* Cart Button */}
              <button
                onClick={() => setIsCartOpen(true)}
                className="action-button"
                style={{
                  position: 'relative',
                  background: 'transparent',
                  border: 'none',
                  cursor: 'pointer',
                  padding: '0.5rem',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  transition: 'all 0.3s ease'
                }}
                aria-label="Open cart"
              >
                <ShoppingCart
                  className="w-6 h-6"
                  style={{ color: cart.length > 0 ? '#6da71d' : '#666' }}
                />
                {cart.length > 0 && (
                  <span
                    className="badge"
                    style={{
                      position: 'absolute',
                      top: '0',
                      right: '0',
                      background: '#6da71d',
                      color: 'white',
                      borderRadius: '50%',
                      width: '18px',
                      height: '18px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      fontSize: '11px',
                      fontWeight: 'bold'
                    }}
                  >
                    {cart.length}
                  </span>
                )}
              </button>
            </div>

            {/* Mobile Menu Toggle */}
            <button 
              className={`mobile-menu-toggle ${isMobileMenuOpen ? 'active' : ''}`}
              onClick={toggleMobileMenu}
            >
              <span></span>
              <span></span>
              <span></span>
            </button>
          </div>
        </div>
      </header>

      {/* Mobile Navigation */}
      <nav className={`mobile-nav ${isMobileMenuOpen ? 'active' : ''}`}>
        <ul className="mobile-menu">
          <li><a href="/">HOME</a></li>
          <li className="has-submenu">
            <a href="/shop" onClick={toggleSubmenu}>SHOP</a>
            <ul className="submenu">
              <li><a href="/products">All Products</a></li>
              <li><a href="/collections">Collections</a></li>
              <li><a href="/collections/T-Shirts">T-Shirts</a></li>
              <li><a href="/collections/Hoodies">Hoodies</a></li>
              <li><a href="/collections/Caps">Caps & Hats</a></li>
              <li><a href="/collections/Bags">Bags</a></li>
            </ul>
          </li>
          <li className="has-submenu">
            <a href="#" onClick={toggleSubmenu}>SERVICES</a>
            <ul className="submenu">
              <li><a href="#">Custom Printing</a></li>
              <li><a href="#">Embroidery</a></li>
              <li><a href="#">Signage</a></li>
              <li><a href="#">Vehicle Graphics</a></li>
            </ul>
          </li>
          <li><a href="#">ABOUT</a></li>
          <li><a href="#">CONTACT</a></li>
        </ul>
      </nav>

      {/* Cart Drawer */}
      <CartDrawer isOpen={isCartOpen} onClose={() => setIsCartOpen(false)} />

      {/* Wishlist Drawer */}
      <WishlistDrawer isOpen={isWishlistOpen} onClose={() => setIsWishlistOpen(false)} />
    </>
  );
};

export default Header;
